import React, { Component } from 'react';

import './styles.scss';

import countries from '../../constants/countries';

class Countries extends Component {

  filterCountries = input => countries.filter(country => country.name.includes(input));

  render() {
    console.log(this.filterCountries("ar"));
    return (
    <main className="main-container">
      <h1 className="countries-title">Paises</h1>
      <ul className="countries-container">
        {
          countries.map(country => (
            <li className="country">
              <img className="country-image" src={country.flag} alt="country flag" />
              <h2 className="country-name">{country.name}</h2>
              <div className="country-info-wrapper">
                <p className="country-info-region">{country.region}</p>
                <p className="country-info-population">{country.population}</p>
              </div>
            </li>
          ))
        }
      </ul>
    </main>
    );
  }
}

export default Countries;
