import React, { Component } from 'react';

import './styles.scss';

import countries from '../../constants/countries';

import CountryFilterForm from '../../components/CountryFilterForm';

class Countries extends Component {
  state = {
    countries
  };

  filterCountries = input => {
    console.log(input)
    const filteredCountries = countries.filter(country => country.name.toLowerCase().includes(input.toLowerCase()));
    this.setState({countries: filteredCountries});
  }

  render() {
    return (
    <main className="main-container">
      <h1 className="countries-title">Paises</h1>
      <CountryFilterForm onSubmit={this.filterCountries} />
      <ul className="countries-container">
        {
          this.state.countries.map(country => (
            <li className="country" key={country.name}>
              <img className="country-image" src={country.flag} alt="country flag" />
              <h2 className="country-name">{country.name}</h2>
              <div className="country-info-wrapper">
                <p className="country-info-region">{country.region}</p>
                <p className="country-info-population">{country.population}</p>
              </div>
            </li>
          ))
        }
      </ul>
    </main>
    );
  }
}

export default Countries;
