import React, { Component } from 'react';
import { PropTypes } from 'prop-types';

import './styles.scss';

const invalidFormat = /[!@#$%^&*(),.?":{}|<>|\d]/g;

class CountryFilterForm extends Component {
  state = {
    search: ''
  };

  isValidCountryFormat = (country) => !invalidFormat.exec(country);

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.onSubmit(this.state.search);
  }

  render() {
    return (
      <form className="search-container" noValidate onSubmit={this.handleSubmit}>
        <input name="search" className="search-input" type="text" placeholder="Ingrese el nombre del paÃ­s a buscar" />
        <button className="search-button" type="submit">Buscar</button>
      </form>
    )
  }
}

CountryFilterForm.propTypes = {
  onSubmit: PropTypes.func
}

export default CountryFilterForm;
